CREATE TABLE `saloon`.`contact_form` (`message_id` INT NOT NULL AUTO_INCREMENT , `fullName` VARCHAR(20) NOT NULL , `contact` VARCHAR(15) NOT NULL , `email` VARCHAR(30) NOT NULL , `subject` VARCHAR(50) NOT NULL , `message` VARCHAR(200) NOT NULL , `user_ip` VARCHAR(20) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP , PRIMARY KEY (`message_id`)) ENGINE = InnoDB;
